import React, {useState, useMemo, Component} from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { Form, Button} from 'react-bootstrap';
import { useTable } from "react-table";
import axios from 'axios';

class Table extends Component {
    constructor(dataa) {
       super(dataa) //since we are extending class Table so we have to use super in order to override Component class constructor
       this.state = { //state is by default an object
          data: [dataa]
       }
    }
 
    renderTableData() {
        return this.state.data.map((data1, index) => {
           const { id, name, location, is_folded } = data1 //destructuring
           return (
              <tr key={id}>
                 <td>{id}</td>
                 <h1>React Dynamic Table</h1>
                 <td>{name}</td>
                 <td>{location}</td>
                 <td>{is_folded}</td>
              </tr>
           )
        })
     }

    render() {
        return (
           <div>
              <h1 id='title'>React Dynamic Table</h1>
              <table id='data'>
                 <tbody>
                    {renderTableData}
                 </tbody>
              </table>
           </div>
        )
     }
    } 


    export default Table;
