import React, {useState, useMemo} from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { Form, Button} from 'react-bootstrap';
import { useTable } from "react-table";
import axios from 'axios';



const Table = ({ dataa }) => {
    const auth = getAuth();
    
    

    const columns = useMemo(
      () => [
        { Header: "ID", accessor: "id" },
        { Header: "Name", accessor: "name" },
        { Header: "Location", accessor: "location" },
        { Header: "Folded", accessor: "is_folded" },
      ],
      []
    ); 

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
      } = useTable({ columns, dataa }); 


      <div className="table-container">
                        <table {...getTableProps()}>
                        <thead>
                        {headerGroups.map((headerGroup) => (
                       <tr {...headerGroup.getHeaderGroupProps()}>
                          {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                          ))}
                        </tr>
                      ))}
                    </thead>
                <tbody {...getTableBodyProps()}>
              {rows.map((row) => {
                     prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                        {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
    
    } 

const Users = () => {
    const auth = getAuth();
    
    
    const baseURL = "http://localhost:5000/api/awnings/" + auth.currentUser.email;
    const [dataa, setData] = useState([]);

    React.useEffect(() => {
        axios.get(baseURL).then((response) => {
          setData(response.data);
        });
      }, []);
      
   
    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <h1 style={{fontFamily:'Josefin Sans'}}>Your awnings</h1>
                    
                    <div>
                    <form>
                    <Button variant="contained" style={{backgroundColor: "black", color:"white"}}>Synchronize awnings</Button>
                    </form>
                    
                    <Table/>
                    </div>
                    
                </Col>
            </Row>
        </Container>
    );
    
};

export default Users;
