import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import NavBar from './components/NavBar';

import Home from './components/Home';
import Footer from './components/Footer';
import SignUp from './components/SignUp';
import LogIn from './components/LogIn';
import WindGraph from './components/WindGraph';
import Users from './components/User';
import LogOut from './components/LogOut';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { useState, useEffect, getUsers } from 'react';
import NavBarLoggedIn from "./components/NavBarLoggedIn.js";


function App() {
    const auth = getAuth();
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const authStatus = auth.currentUser;

    const setAuth = (value) => {
    setIsAuthenticated(value);
    //alert(value);
    };
    useEffect(()=>{
    setIsAuthenticated(authStatus)
  },[])
    return (
        
        <Router>
            <div className="App">
                {isAuthenticated !== false ? 
                    <NavBarLoggedIn /> : <NavBar/>}
                
                
                
                
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/signup" element={<SignUp />} />
                    <Route path="/login" element={<LogIn />} />
                    <Route path="/logout" element={<LogOut />} />
                    <Route path="/wind-graph" element={<WindGraph />} />
                    <Route path="/user/:userId" element={<Users />} />
                </Routes>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
