import React, {useState} from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import { styled } from '@mui/material/styles';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { getAuth, onAuthStateChanged } from "firebase/auth";

const Awning = () => {

    const { id }= useParams()
    const auth = getAuth();
    const [dataa, setData] = useState([]);
    const [lastSpeed, setLastSpeed] = useState([]);
    const [time, setTime] = useState([]);

    React.useEffect(() => {
        
        axios.get("http://localhost:5000/api/awnings/"+id + "/" + auth.currentUser.email).then((response) => {
          setData(response.data);
          
        });

        axios.get("http://localhost:5000/api/wind-data/last/" + id).then((response) => {
            setLastSpeed(response.data);
            setTime(new Intl.DateTimeFormat('en-US', {year: 'numeric', month: '2-digit',day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'}).format(response.data.createdAt));
        })
      }, []);
    
    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <h1 style={{fontFamily:'Josefin Sans', marginTop:"0px", fontSize:"50px"}}>{dataa.name}</h1>
                    <p style={{fontSize: "30px", fontFamily:'Josefin Sans'}}>Location: {dataa.location}</p>
                    <p style={{fontSize: "30px", fontFamily:'Josefin Sans'}}>Folded: {dataa.is_folded ? "Yes" : "No"}</p>
                    <p style = {{fontSize: "30px", fontFamily:'Josefin Sans'}} > Last speed: {lastSpeed.speed}km/h, recorded at: {lastSpeed.createdAt}</p>
                    
                </Col>
            </Row>
        </Container>
    );
};

export default Awning;
