import React, {useState, useMemo} from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { Form, Button} from 'react-bootstrap';
import { useTable } from "react-table";
import axios from 'axios';
import Table from './Table';





const Users = () => {
    
    
    const auth = getAuth();
    
    const [dataa, setData] = useState([]);

    React.useEffect(() => {
        
        axios.get("http://localhost:5000/api/awnings/"+auth.currentUser.email).then((response) => {
          setData(response.data);
          
        });
      }, []);
      
   
    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <h1 style={{fontFamily:'Josefin Sans'}}>Your awnings</h1>
                    
                    <div>
                    <form>
                    <Button variant="contained" style={{backgroundColor: "black", color:"white"}}>Synchronize awnings</Button>
                    </form>
                    <table id='data' style={{border: '1px solid', padding: '15px',
  textAlign: 'left',}}>
                 <tbody >
                 <tr style ={{padding: '15px',
  textAlign: 'left'}}><th>ID</th> <th>Name</th> <th>Location</th> <th>Is Folded</th> </tr>
                    {Object.entries(dataa).map(([key,value]) => (<div className="item" key={key}>
            
          <tr key={{key}} ><td>{value.id}</td> <td> {value.name} </td> <td>{value.location}</td> <td> {value.is_folded} </td> </tr>
         </div>))}
         </tbody>
         </table>
                    </div>
                    
                </Col>
            </Row>
        </Container>
    );
    
};



export default Users;
