import React, {useState} from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getAuth, onAuthStateChanged } from "firebase/auth";
import { Form, Button} from 'react-bootstrap';
import axios from 'axios';



const Users = () => {
    const auth = getAuth();
    
    const baseURL = "http://localhost:5000/api/awnings/" + auth.currentUser.email;
    const [data, setData] = useState([]);

    React.useEffect(() => {
        axios.get(baseURL).then((response) => {
          setData(response.data);
        });
      }, []);
   
    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <h1 style={{fontFamily:'Josefin Sans'}}>Your awnings</h1>
                    <p>{data}</p>
                    <div>
                    <form>
                    <Button variant="contained" style={{backgroundColor: "black", color:"white"}}>Synchronize awnings</Button>
                    </form>

                    
                    </div>
                    
                </Col>
            </Row>
        </Container>
    );
    
};

export default Users;
